function matrix=Contraire_jUpperT(vector,length_matrix, diago)%vector: values convert to matrix%length: size of one side of the matrix%diago:% 0: some vector values are on the diagonal% 1: no vlaues in the diagonalidx=find(triu(ones(length_matrix,length_matrix),diago)==1); % contraire de jUpperTriMatToVecmatrix=zeros(length_matrix,length_matrix);matrix(idx)=vector;matrix=matrix+(matrix-diag(diag(matrix))).';